import { CstNode, ICstVisitor, IToken } from 'chevrotain'
import { Integer, lex, Token, tokenVocabulary } from './lexer'
import { SelectParser } from './parser'
import { Field, Operand, Order, ordering, From, SQLTree } from './SqlTree'

const parserInstance = new SelectParser()
const BaseSQLVisitor: new (arg?: any) => ICstVisitor<number, any> = parserInstance.getBaseCstVisitorConstructor()

class SQLToAstVisitor extends BaseSQLVisitor {
    constructor() {
        super()
        this.validateVisitor()
    }

    public selectStatement(ctx: {
        selectClause: CstNode | CstNode[]
        fromClause: CstNode | CstNode[]
        whereClause: CstNode | CstNode[]
        orderByClause: CstNode | CstNode[]
        limitClause: CstNode | CstNode[]
    }) {
        const select = this.visit(ctx.selectClause)
        const from = this.visit(ctx.fromClause)
        const where = this.visit(ctx.whereClause)
        const order = this.visit(ctx.orderByClause)
        const limit = this.visit(ctx.limitClause)
        return {
            fields: select,
            source: from,
            where,
            order,
            limit,
        } as const
    }

    public selectClause(ctx: { projection: CstNode | CstNode[] }) {
        const columns = this.visit(ctx.projection)
        return columns
    }

    public cols(ctx: { name: IToken[]; value: IToken[] }) {
        let value = ctx.value[0].image

        if (ctx.value[0].tokenType === tokenVocabulary.StringToken) {
            value = convertStringTokenToJsString(value)
        }

        let name = ctx.name && ctx.name[0] ? ctx.name[0].image : value

        if (ctx.name && ctx.name[0].tokenType === tokenVocabulary.StringToken) {
            name = convertStringTokenToJsString(name)
        }
        return {
            name,
            value,
        }
    }

    public projection(ctx: { cols: CstNode[] }) {
        const cols: { value: string; name: string }[] = ctx.cols.map(x => this.visit(x)) as any
        const fields: Field[] = []
        cols.forEach(({ name, value }) => {
            const { pathArray: namePathArray, propertyName: namePropertyName } = splitPropertyPath(name)
            const { pathArray: fieldPathArray, propertyName: fieldPropertyName } = splitPropertyPath(value)
            const field: Field = {
                name: {
                    value: namePropertyName,
                    values: namePathArray,
                },
                field: {
                    value: fieldPropertyName,
                    values: fieldPathArray,
                },
            }
            fields.push(field)
        })
        return fields
    }

    public fromClause(ctx: { table: Array<IToken>; alias: Array<IToken> }): From {
        let tableName: string = ctx.table[0].image
        if (ctx.table[0].tokenType === tokenVocabulary.StringToken) {
            tableName = convertStringTokenToJsString(tableName)
        }
        const alias = ctx.alias?.length ? ctx.alias[0].image : tableName
        return {
            name: {
                value: tableName,
                values: splitPropertyPath(tableName).pathArray,
            },
            alias: {
                value: alias,
                values: splitPropertyPath(alias).pathArray,
            },
        }
    }

    public whereClause(ctx: { expression: CstNode | CstNode[] }) {
        const conditions = this.visit(ctx.expression)

        return {
            conditions,
        } as const
    }

    public limitClause(ctx: { Integer: IToken[] }) {
        const limit = parseInt(ctx.Integer[0].image)
        return {
            value: {
                value: limit,
            },
        }
    }
    public orderByClause(ctx: { OrderBy: IToken[]; Identifier: IToken[]; OrderByDirection: IToken[] }): Order {
        const { pathArray, propertyName } = splitPropertyPath(ctx.Identifier[0].image)
        const direction = ctx.OrderByDirection && (ctx.OrderByDirection[0].image as 'asc' | 'desc')

        const order: ordering = {
            value: {
                value: propertyName,
                values: pathArray,
            },
            direction,
        }

        return {
            orderings: [order],
        }
    }

    public expression(
        ctx: {
            subExpression: Array<CstNode | CstNode[]>
            OrAnd: Array<IToken>
            right: Array<CstNode | CstNode[]>
        },
        i: number = 0
    ) {
        if (ctx.subExpression?.length - i === 1) {
            const left = this.visit(ctx.subExpression[i])
            return left
        }
        const left = this.visit(ctx.subExpression[i])

        const operation = this.OrAnd(ctx.OrAnd[i])
        const right: Operand = this.expression(ctx, ++i)

        return {
            left,
            right,
            operation,
        }
    }

    public subExpression(ctx: {
        left: Array<CstNode | CstNode[]>
        relationalOperator: CstNode | CstNode[]
        right: Array<CstNode | CstNode[]>
    }) {
        const left = this.visit(ctx.left[0])
        const operation = this.visit(ctx.relationalOperator)
        const right = this.visit(ctx.right[0])

        return {
            left: { value: left, values: splitPropertyPath(left).pathArray },
            operation,
            right: { value: right, values: splitPropertyPath(right).pathArray },
        }
    }

    public atomicExpression(context: Record<Token | 'in', Array<IToken>>) {
        const entries = Object.entries(context) as [keyof typeof context, Array<IToken>][]
        for (let [key, value] of entries) {
            if (key === 'in') {
                return value
                    .map(x => {
                        if (x.tokenType === Integer) {
                            return parseInt(x.image)
                        }
                        return convertStringTokenToJsString(x.image)
                    })
                    .map(value => ({ value }))
            }
            if (key === 'Integer') {
                return parseInt(value[0].image)
            }
            if (key === 'Null') {
                return null
            }
            if (key === 'Identifier') {
                return value[0].image
            }
            if (key === 'StringToken') {
                return convertStringTokenToJsString(value[0].image)
            }
        }
        return null
    }

    public relationalOperator(ctx: Record<Token, Array<IToken>>) {
        const values = Object.values(ctx)
        return values[0][0].image
    }

    public OrAnd(token: IToken) {
        return token.image
    }
}

const toAstVisitorInstance = new SQLToAstVisitor()

const toAst = (inputText: string) => {
    const lexResult = lex(inputText)

    parserInstance.input = lexResult.tokens
    let cst: CstNode[] | CstNode

    cst = parserInstance.selectStatement()

    if (parserInstance.errors.length > 0) {
        throw Error(JSON.stringify(parserInstance.errors))
    }
    const ast = toAstVisitorInstance.visit(cst)
    return (ast as unknown) as SQLTree
}

const splitPropertyPath = (stringPath: string | number) => {
    const pathArray = String(stringPath).split('.')

    return { propertyName: pathArray[pathArray.length - 1], pathArray }
}

const convertStringTokenToJsString = (str: string) => str.substring(1, str.length - 1)

export { toAst }
import _ from 'lodash'
import { jsonParseSafe } from './converters/json'
import { toAst } from './sql/actions-visitor'
import { Field, Operand, From, SQLTree } from './sql/SqlTree'
export const computePath = (path: string[] | undefined) => {
    if (!path) {
        return []
    }
    if (path[0] === 'data') {
        const newPath = [...path]
        newPath.shift()
        return newPath
    }

    return path
}

export const sqlEvaluation = (sourceString: string, queryString: string) => {
    try {
        const sqlTree = toAst(cleanComment(queryString))
        if (sqlTree.source.name.values && sqlTree.source.name.values[0] !== 'data') {
            return new SyntaxError(String(sqlTree.source.name.values[0]))
        }

        const sourceDataObject = jsonParseSafe(sourceString)

        const result = executeQuery(sqlTree, sourceDataObject)

        return JSON.stringify(result)
    } catch (e) {
        return e as Error
    }
}

const cleanComment = (str: string) => str.replace(/--(.*?)(\n|$)/gm, '')

const map = (v: object, fields: Field[], source: From) => {
    if (fields.some(x => x.field.value === '*')) {
        return v
    }

    return mapObject(fields, v, source)
}

const executeQuery = (sqlTree: SQLTree, sourceDataObject: object) => {
    let fromPath: (string | number)[] = [...sqlTree.source.name.values]
    fromPath.shift()

    let result = _.chain<any>(sourceDataObject)

    if (fromPath && fromPath.length > 0) {
        result = _.chain<any>(result.get(fromPath))
    }
    const value = result.value()
    if (_.isArray(value)) {
        return result
            .filter(v => {
                if (!sqlTree.where || !sqlTree.where.conditions) {
                    return true
                }
                const leftValue = sqlTree.where.conditions.left
                const rightValue = sqlTree.where.conditions.right
                const operation = sqlTree.where.conditions.operation

                return compareOperands(operation, leftValue, rightValue, v)
            })
            .orderBy(
                sqlTree.order ? sqlTree.order.orderings.map(x => x.value.value) : undefined,
                sqlTree.order ? sqlTree.order.orderings.map(x => x.direction) : undefined
            )
            .map(v => map(v, sqlTree.fields, sqlTree.source))
            .take(sqlTree.limit?.value?.value ? parseInt(sqlTree.limit.value.value.toString()) : 999999999999999)
            .value()
    }
    return map(value, sqlTree.fields, sqlTree.source)
}
const operators = {
    modulo: '%',
} as const
const compareOperands = (operation: string, left: Operand, right: Operand, value: object): boolean => {
    const leftValue = _.get(value, computePath(left?.values))

    switch (operation.toLowerCase()) {
        case 'or':
            return (
                compareOperands(left.operation, left.left, left.right, value) ||
                compareOperands(right.operation, right.left, right.right, value)
            )
        case 'and':
            return (
                compareOperands(left.operation, left.left, left.right, value) &&
                compareOperands(right.operation, right.left, right.right, value)
            )

        case '=':
        case 'is':
            return leftValue === right.value
        case '!=':
        case 'is not':
        case '<>':
            return leftValue !== right.value
        case 'like': {
            const leftStr = String(leftValue)
            const rightStr = String(right.value)
            if (rightStr.startsWith(operators.modulo) && rightStr.endsWith(operators.modulo)) {
                if (leftStr.includes(rightStr.substring(1, rightStr.length - 1))) {
                    return true
                }
            } else if (rightStr.startsWith(operators.modulo)) {
                if (leftStr.endsWith(rightStr.substring(rightStr.indexOf(operators.modulo) + 1))) {
                    return true
                }
            } else if (rightStr.endsWith(operators.modulo)) {
                if (leftStr.startsWith(rightStr.substring(0, rightStr.indexOf(operators.modulo)))) {
                    return true
                }
            }
            return false
        }
        case 'not like': {
            const leftStr = String(leftValue)
            const rightStr = String(right.value)
            if (rightStr.startsWith(operators.modulo) && rightStr.endsWith(operators.modulo)) {
                if (leftStr.includes(rightStr.substring(1, rightStr.length - 1))) {
                    return false
                }
            } else if (rightStr.startsWith(operators.modulo)) {
                if (leftStr.endsWith(rightStr.substring(rightStr.indexOf(operators.modulo) + 1))) {
                    return false
                }
            } else if (rightStr.endsWith(operators.modulo)) {
                if (leftStr.startsWith(rightStr.substring(0, rightStr.indexOf(operators.modulo)))) {
                    return false
                }
            }
            return true
        }
        case '>':
            return !!right.value && leftValue > right.value
        case '>=':
            return !!right.value && leftValue >= right.value
        case '<':
            return !!right.value && leftValue < right.value
        case '<=':
            return !!right.value && leftValue <= right.value
        case 'in':
            return Array.isArray(right.value) && right.value.filter(x => x.value === leftValue).length > 0
    }
    return false
}

const mapObject = (fields: Field[], mapped: object, source: From) => {
    const temp: {
        [key: string]: any
    } = {}
    fields.forEach(field => {
        const value = _.get(
            mapped,
            field.field.values.filter(val => val !== source.alias?.value)
        )
        temp[field.name.value] = value
    })
    mapped = temp
    return mapped
}
export const jsonBeautify = (str: string | undefined) => {
    if (typeof str !== 'string') {
        return ''
    }

    const parsed = jsonParseSafe(str)
    if (typeof parsed === 'string') {
        return parsed
    }

    if (parsed === null) {
        return ''
    }

    return JSON.stringify(parsed, null, 2)
}

export const jsonParseSafe = (str: string | null) => {
    if (typeof str !== 'string' || str.trim() === '') {
        return null
    }

    const safeStr = str
        .replace(/\\n/g, '\\n')
        .replace(/\\'/g, "\\'")
        .replace(/\\"/g, '\\"')
        .replace(/\\&/g, '\\&')
        .replace(/\\r/g, '\\r')
        .replace(/\\t/g, '\\t')
        .replace(/\\b/g, '\\b')
        .replace(/\\f/g, '\\f')

    try {
        return JSON.parse(safeStr)
    } catch (error) {
        return str
    }
}
